#include <stdlib.h>
#include <stdio.h>
#include "str.h"

static void test(char *p, unsigned int h) {
	str s;
	str_init(&s, p);
	unsigned int t = str_hash(&s);
	if (t != h) {
		printf("%s:%i test failed: %u != %u (string '%s')\n", __FILE__, __LINE__, t, h, p);
		abort();
	}
}

int main() {
	test("", CONST_STR_HASH(""));
	test("a", CONST_STR_HASH("a"));
	test("ABC", CONST_STR_HASH("ABC"));
	test("test test 1 2 3", CONST_STR_HASH("test test 1 2 3"));
	test("", CONST_STR_HASH(""));
	return 0;
}
